INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (7, '26f6abfa0d7725fef678e371897d5df0', 'nhung-ngoi-sao-nhac-co-dien', 'Những Ngôi Sao Nhạc Cổ Điển', 'Classic Stars', 'https://phimimg.com/upload/vod/20250407-1/6517e015b88e3e276d13996b4b3f5a9a.jpg', 'https://phimimg.com/upload/vod/20250407-1/b04f5e321f28ee2d17d35abc2e9f1765.jpg', 2025, 'hoathinh', 'FHD', 'Vietsub', '23 phút/tập', 0, 'Tại Học viện Tư thục Gloria – nơi quy tụ những tài năng trẻ được kỳ vọng trong tương lai trong các lĩnh vực âm nhạc, nghệ thuật, thể thao và giải trí – một dự án đặc biệt đang diễn ra. Tại khoa Âm nhạc, những học sinh có mức độ tương thích cao với “tài năng (tiềm năng)” của các nhà soạn nhạc vĩ đại trong quá khứ sẽ được cấy ghép “Gift” (món quà), và từ đó được gọi bằng tên của chính nhà soạn nhạc đó. Beethoven, một chàng trai trẻ tình cờ được phát hiện có mức độ tương thích sau một chấn thương, cũng trở thành một trong số họ. Cậu chuyển vào học viện và bắt đầu làm quen với những người bạn mang các “Gift” khác nhau. Cùng nhau, họ trải nghiệm sức hấp dẫn của âm nhạc và hướng tới chiến thắng trong các cuộc thi âm nhạc lớn. Một bản giao hưởng kỳ diệu nơi âm nhạc (Emotion) và không gian thực tế ảo (Visualize) hòa quyện – một **dự án mộng mơ nối tiếp ý chí của những nhạc sĩ vĩ đại tới tương lai – chính thức bắt đầu!', 'ongoing', 'https://www.youtube.com/watch?v=BnI0BqkjOrw', NULL, NULL, false, '[{"id": "68564911f00849030f9c9c144ea1b931", "name": "Viễn Tưởng", "slug": "vien-tuong"}, {"id": "66c78b23908113d478d8d85390a244b4", "name": "Phiêu Lưu", "slug": "phieu-luu"}, {"id": "37a7b38b6184a5ebd3c43015aa20709d", "name": "Chính Kịch", "slug": "chinh-kich"}]', '[{"id": "d4097fbffa8f7149a61281437171eb83", "name": "Nhật Bản", "slug": "nhat-ban"}]', 'Đang cập nh