INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (60, 'ed3fc73e6c5e6b0723e9e7935f50947a', 'nha-tro-nhat-khac', 'Nhà Trọ Nhất Khắc', 'Maison Ikkoku', 'https://phimimg.com/upload/vod/20250524-1/344079ebf0ce0e8d99c273fb4b0f4783.jpg', 'https://phimimg.com/upload/vod/20250524-1/15aa749fcb5606f5e3ffa9730db4eee5.jpg', 1986, 'hoathinh', 'FHD', 'Vietsub', '26 phút/tập', 0, 'Như cái tên của tác phẩm đã nêu, câu chuyện xảy ra tại một nhà trọ mang tên là Nhất Khắc (Ikkoku). Người quản lý nhà trọ trước đó đã quyết định nghỉ hưu, và ông cử một phụ nữ trẻ tên là Otonashi Kyoko đến thay thế mình. Người quản lý xinh đẹp đã khiến Godai Yusaku, người học trò nghèo trọ ở phòng 5 thầm yêu thương. Tuy nhiên, Yusaku gặp rất nhiều khó khăn trong việc bày tỏ tình cảm với Kyoko. Những lần anh tỏ tình với Kyoko hầu như đều thất bại: hoặc anh bị người khác vô tình làm gián đoạn, hoặc anh làm Kyoko hiểu lầm và gây ra những tình huống dở khóc dở cười. Mọi chuyện trở nên rắc rối hơn khi Kyoko gia nhập một câu lạc bộ quần vợt dành cho phụ nữ, ở đó cô gặp Mitaka Shun, một người thanh niên giàu có, đẹp trai nhưng tốt bụng. Shun thực lòng yêu thương Kyoko và mong cô kết hôn với anh, nhưng Kyoko cảm thấy khó xử khi đứng trước tình cảm của Yusaku lẫn Shun. Tiếp đó, Nikaido Nozomu, một cậu học sinh 18 tuổi gia nhập &quot;cộng đồng&quot; Ikkoku để rồi cũng thầm thương cô quản lý xinh đẹp của khu nhà trọ. Và một điều ít ai biết là Kyoko đã từng có một người chồng yểu mệnh tên là Otonashi Soichiro, anh qua đời chỉ sáu tháng sau khi hai người kết hôn và hình ảnh người chồng quá cố vẫn còn rất sâu đậm trong trái tim Kyoko. Rõ ràng, đối với Yusaku, một người học trò nghèo, cục mịch và gặp nhiều lận đận trong thi cử, việc có được trái tim của Kyoko không hề đơn giản...', 'completed', 'https://www.youtube.com/watch?v=tuDvCVM0Qb8', NULL, NULL, false, '[{"id": "ba6fd52e5a3aca80eaaf1a3b50a182db", "name": "Hài Hước", "slug": "hai-huoc"}, {"id": "bb2b4b030608ca5984c8dd0770f5b40b", "name": "Tình Cảm", "slug": "tinh-cam"}, {"id": "a7b065b92ad356387ef2e075dee66529", "name": "Tâm Lý", "slug": "tam-ly"}]', '[{"id": "d4097fbffa8f7149a61281437171eb83", "name": "Nhật Bản", "slug": "nhat-ban"}]', 'Issei Futamata,Sumi Shimamoto,Shigeru Chiba,Yuko Mita,Kazuyo Aoki,Akira Kamiya,Norio Wakamoto,Toshio Furukawa,Hiromi Tsuru,Minori Matsushima,Chika Sakamoto,Hisako Kyoda,Kazue Komiya,Kosei Tomita,Anna Cummer,Alexandra Carter,Andrea Libman,', 'Đang cập nhật', '2025-05-24 17:34:40.54', 'Hoàn Tất (96/96)', '96');
