INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (64, '0b7361b9b59ab639eb91b016ddb87421', 'hoa-dai-dong-noi-2025', 'Hoa Dại Đồng Nội', 'Wild Flowers', 'https://phimimg.com/upload/vod/20250524-1/2fe245641959890f0f237354c30070df.jpg', 'https://phimimg.com/upload/vod/20250524-1/ee6e6148fbf148b24432754dd223d528.jpg', 2023, 'single', 'FHD', 'Vietsub', '91 phút', 0, 'Kevin là một nam diễn viên điện ảnh quyến rũ, hiện đang quay phim tại một địa điểm hẻo lánh. Khi công việc kết thúc, họ đã tổ chức một buổi tiệc với dàn diễn viên, ngập tràn rượu và ma túy. Sau một đêm vui vẻ và ăn chơi thác loạn, Kevin khăng khăng muốn trở về thành phố, bất chấp những lời cảnh báo và khuyên ngăn của các đồng nghiệp rằng điều đó có thể rất nguy hiểm. Và như thể họ đã dự đoán trước điều gì sẽ xảy ra, Kevin mất lái khi đang lái xe và gặp tai nạn nghiêm trọng…', 'completed', 'https://www.youtube.com/watch?v=zKb7DI2zNiY', NULL, NULL, false, '[{"id": "a7b065b92ad356387ef2e075dee66529", "name": "Tâm Lý", "slug": "tam-ly"}, {"id": "bb2b4b030608ca5984c8dd0770f5b40b", "name": "Tình Cảm", "slug": "tinh-cam"}, {"id": "37a7b38b6184a5ebd3c43015aa20709d", "name": "Chính Kịch", "slug": "chinh-kich"}]', '[{"id": "77dab2f81a6c8c9136efba7ab2c4c0f2", "name": "Philippines", "slug": "philippines"}]', 'Arron Villaflor,Sheila Snow,Chloe Jenna,Jeric Raval,Sheree,Marissa Sanchez,Gerald Madrid,Dominic Roco,Raul Morit,Dianne Moldez,Van Allen Ong,Chloe Mendoza', 'Jeffrey Hidalgo', '2025-05-24 17:34:55.679', 'Full', '1');
