INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (75, '4ba3a8aaad152d230887da6a26610846', 'doraemon-nobita-va-ba-chang-hiep-si-mong-mo', 'Doraemon: Nobita và Ba Chàng Hiệp Sĩ Mộng Mơ', 'Doraemon: Nobita''s Three Visionary Swordsmen', 'https://phimimg.com/upload/vod/20250521-1/3ea1c9deeb19a16f472c78164d42f1d0.jpg', 'https://phimimg.com/upload/vod/20250521-1/f4a1392b6675476fb8d21857ba9c67e1.jpg', 1994, 'hoathinh', 'FHD', 'Lồng Tiếng', '99 phút', 0, 'Mệt mỏi vì liên tục gặp ác mộng, Nobita yêu cầu Doraemon mang đến một Cỗ máy ước mơ để Nobita có thể mơ bất cứ điều gì cậu muốn. Sau khi nỗ lực để có được một giấc mơ về sự sụp đổ của Atlantis không thành công, anh ta chọn một giấc mơ nơi anh ta đang ở trong một thế giới giả tưởng với nguồn cảm hứng từ &quot;Ba chàng lính ngự lâm&quot;. Nobita và những người bạn của mình phải cùng nhau đánh bại tên Vua độc ác trước khi hắn thoát khỏi giấc mơ của Nobita để đi vào giấc mơ của người khác.', 'completed', '', NULL, NULL, false, '[{"id": "a2492d6cbc4d58f115406ca14e5ec7b6", "name": "Gia Đình", "slug": "gia-dinh"}, {"id": "66c78b23908113d478d8d85390a244b4", "name": "Phiêu Lưu", "slug": "phieu-luu"}]', '[{"id": "d4097fbffa8f7149a61281437171eb83", "name": "Nhật Bản", "slug": "nhat-ban"}]', 'Nobuyo Oyama,Noriko Ohara,Michiko Nomura,Kaneta Kimotsuki,Kazuya Tatekabe', 'Tsutomu Shibayama', '2025-05-24 17:36:07.255', 'Full', '1');
INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (70, '65df376cb19d1a208ea312d7f58c7579', 'doraemon-nobita-va-nhung-phap-su-gio-bi-an', 'Doraemon: Nobita và Những Pháp Sư Gió Bí Ẩn', 'Doraemon: Nobita and the Windmasters', 'https://phimimg.com/upload/vod/20250521-1/b868b6bdf13baf0a6cc90aaa8d022371.jpg', 'https://phimimg.com/upload/vod/20250521-1/c7241c40bbe8fba3ab85cf19c0ea501c.jpg', 2003, 'hoathinh', 'FHD', 'Lồng Tiếng', '84 phút', 0, 'Một ngày nọ, Nobita tình cờ gặp một quả cầu gió giống như một sinh vật đến từ một vùng đất xa xôi, bị thổi bay vào khu vực lân cận của họ sau cơn bão gần đây đi qua khu vực này. Nobita đặt tên cho nó là Fuuko. Cuối cùng, cậu, Doraemon và những người bạn của họ đưa nó đến một vùng nông thôn rộng lớn qua Cánh cửa bất kỳ. Tại đó, họ tìm hiểu về Làng Gió và Bão, và danh tính thực của Fuuko. Chẳng bao lâu nữa, Nobita và những người bạn của cậu phải bảo vệ Fuuko khỏi Làng Bão và Nobuo giờ đã bị chiếm hữu, người đang bị điều khiển bởi linh hồn của thủ lĩnh cũ của Làng Bão.', 'completed', '', NULL, NULL, false, '[{"id": "68564911f00849030f9c9c144ea1b931", "name": "Viễn Tưởng", "slug": "vien-tuong"}, {"id": "66c78b23908113d478d8d85390a244b4", "name": "Phiêu Lưu", "slug": "phieu-luu"}, {"id": "a2492d6cbc4d58f115406ca14e5ec7b6", "name": "Gia Đình", "slug": "gia-dinh"}, {"id": "0bcf4077916678de9b48c89221fcf8ae", "name": "Khoa Học", "slug": "khoa-hoc"}, {"id": "68564911f00849030f9c9c144ea1b931", "name": "Viễn Tưởng", "slug": "vien-tuong"}]', '[{"id": "d4097fbffa8f7149a61281437171eb83", "name": "Nhật Bản", "slug": "nhat-ban"}]', 'Michiko Nomura,Nobuyo Oyama,Noriko Ohara,Kaneta Kimotsuki,Kazuya Tatekabe', 'Tsutomu Shibayama', '2025-05-24 17:35:39.81', 'Full', '1');
