INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (79, '34b192408688fdd73d2f323cb052d692', 'doraemon-nobita-va-nhung-dung-si-co-canh', 'Doraemon: Nobita và Những Dũng Sĩ Có Cánh', 'Doraemon: Nobita and the Winged Braves', 'https://phimimg.com/upload/vod/20250521-1/82f0cee07b135d83c89f47ca6043f479.jpg', 'https://phimimg.com/upload/vod/20250521-1/c13bc9759142bc85240151f21ce976ee.jpg', 2001, 'hoathinh', 'FHD', 'Vietsub', '91 phút', 0, 'Sau khi xem chương trình truyền hình về loài chim và một loài được cho là có hình người được phát hiện ở Châu Phi, Nobita mơ ước có thể &nbsp;được bay bằng đôi cánh. Cậu ta tạo ra đôi cánh bằng gỗ sau khi Doraemon từ chối giúp đỡ cậu ấy , dù nhiều lần thất bại với đôi cánh tự chế của mình. Trong khi giúp Shizuka lấy lại con chim hoàng yến cưng của mình, họ chứng kiến một cánh cổng mở ra trên bầu trời và một con chim hình người cưỡi máy bay lao ra. Anh ta tự giới thiệu mình là Gusuke, một người chim sống ở &quot;Birdopia&quot;. Doraemon, Nobita và Shizuka giúp Gusuke sửa chiếc máy bay của mình, và Nobita nhanh chóng trở thành bạn của cậu. Khi Gusuke trở về nhà của mình, Gian và Suneo đã bắt được anh ta và lấy máy bay của anh ta. Ba người còn lại theo họ qua cổng', 'completed', '', NULL, NULL, false, '[{"id": "a2492d6cbc4d58f115406ca14e5ec7b6", "name": "Gia Đình", "slug": "gia-dinh"}, {"id": "66c78b23908113d478d8d85390a244b4", "name": "Phiêu Lưu", "slug": "phieu-luu"}, {"id": "0bcf4077916678de9b48c89221fcf8ae", "name": "Khoa Học", "slug": "khoa-hoc"}, {"id": "68564911f00849030f9c9c144ea1b931", "name": "Viễn Tưởng", "slug": "vien-tuong"}, {"id": "68564911f00849030f9c9c144ea1b931", "name": "Viễn Tưởng", "slug": "vien-tuong"}]', '[{"id": "d4097fbffa8f7149a61281437171eb83", "name": "Nhật Bản", "slug": "nh