INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (80, '680c256fb6e2c27e27a9d268e8379690', 'nhat-ky-cua-me', 'Nhật Ký Của Mẹ', 'My Little Old Boy', 'https://phimimg.com/upload/vod/20250309-1/ca480b54cd2ca6060c6b1e5b7f3abceb.jpg', 'https://phimimg.com/upload/vod/20250309-1/68a1b2439bec4f1f79820d4ddd6a1e8b.jpg', 2016, 'tvshows', 'FHD', 'Vietsub', '100 phút/tập', 0, 'Bạn hiểu bao nhiêu về cậu con trai đã trưởng thành của mình? Trong “Nhật Ký Của Mẹ”, cuộc sống thường nhật của những người con trai nổi tiếng nay đã trưởng thành được các bà mẹ theo dõi một cách cẩn thận và đầy yêu thương. Bạn có biết vì sao dạo này con trai mình hay uống rượu không? Bạn hiểu bao nhiêu về triết lý sống và lối sống của con mình? “Nhật Ký Của Mẹ” mang đến những khoảnh khắc xúc động, nơi tình mẫu tử được thể hiện qua sự quan sát và thấu hiểu – một cuộc đối thoại không lời nhưng đầy gắn kết giữa mẹ và con trai.', 'ongoing', 'https://www.youtube.com/watch?v=hDXK_LYvS1M', NULL, NULL, false, '[{"id": "37a7b38b6184a5ebd3c43015aa20709d", "name": "Chính Kịch", "slug": "chinh-kich"}, {"id": "a2492d6cbc4d58f115406ca14e5ec7b6", "name": "Gia Đình", "slug": "gia-dinh"}]', '[{"id": "05de95be5fc404da9680bbb3dd8262e6", "name": "Hàn Quốc", "slug": "han-quoc"}]', 'Shin Dong-yup,Seo Jang-hoon', 'Kwak Seung-young', '2025-05-24 17:36:38.18', 'Tập 443', '480');
INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (81, '7cbc8120edbc9108520a43532f5439a6', 'doraemon-nobita-va-me-cung-thiec', 'Doraemon: Nobita và Mê Cung Thiếc', 'Doraemon: Nobita and the Tin Labyrinth', 'https://phimimg.com/upload/vod/20250521-1/a8cf6f67a982cb577331561b4feb4a44.jpg', 'https://phimimg.com/upload/vod/20250521-1/7460d3da1db05f1bb0e9052b6ea88c37.jpg', 1993, 'hoathinh', 'FHD', 'Vietsub', '100 phút', 0, 'Bố Nobita tình cờ thấy một quảng cáo kỳ lạ về một khu nghỉ dưỡng tuyệt vời trên truyền hình vào lúc nửa đêm. Đang buồn ngủ, anh ấy đặt chỗ trước dù thậm chí không nhận ra mình đang nói chuyện với quảng cáo. Ngày hôm sau, anh thảo luận với gia đình về kế hoạch kỳ nghỉ của họ, chỉ để nhận ra rằng anh không thể tìm thấy nơi nào trên trái đất. Đột nhiên, mặc dù có một chiếc vali trong phòng Nobita và bị tò mò như vậy, cậu mở nó ra chỉ để tìm thấy cánh cổng dẫn đến một khu nghỉ dưỡng xinh đẹp được quản lý bởi những con rô bốt thiếc. Vẫn tốt hơn, nó hoàn toàn miễn phí. Có vẻ như có một chương trình nghị sự ẩn đằng sau người mời họ đến đó.', 'completed', '', NULL, NULL, false, '[{"id": "66c78b23908113d478d8d85390a244b4", "name": "Phiêu Lưu", "slug": "phieu-luu"}, {"id": "a2492d6cbc4d58f115406ca14e5ec7b6", "name": "Gia Đình", "slug": "gia-dinh"}]', '[{"id": "d4097fbffa8f7149a61281437171eb83", "name": "Nhật Bản", "slug": "nhat-ban"}]', 'Noriko Ohara,Michiko Nomura,Kazuya Tatekabe,Sachiko Chijimatsu,Yôsuke Naka,Kaneta Kimotsuki,Nobuyo Oyama', 'Tsutomu Shibayama', '2025-05-24 17:36:47.088', 'Full', '1');
INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (82, '83e1adbd4772ca2758d087bc632ec9eb', 'nguoi-danh-cap-trai-tim-toi', 'Người Đánh Cắp Trái Tim Tôi', 'Took My Heart Away', 'https://phimimg.com/upload/vod/20250523-1/cc708eb0c3c1a3b37d804fc282849d1d.jpg', 'https://phimimg.com/upload/vod/20250523-1/74791e94994d3e4390e48a23408bc1c2.jpg', 2025, 'series', 'FHD', 'Vietsub', '17 phút/tập', 0, 'Phim kể về thủ lĩnh Ảnh Thám – Phương Nhàn, buộc phải che giấu thân phận để kết hôn gả vào hào môn họ Khám. Anh tình cờ kết hôn với Khám Mỹ Viện – người con gái mà anh đã thầm yêu từ lâu nhưng cô lại luôn chống đối cuộc hôn nhân sắp đặt này. Một nụ hôn bất ngờ đã kích hoạt hiện tượng hoán đổi linh hồn, khiến hai người phải miễn cưỡng hợp tác trong hoàn cảnh trớ trêu khi bị hoán đổi thân xác. Khám Mỹ Viện ban đầu cố tình bày mưu gây khó dễ để thoát khỏi cuộc hôn nhân, nhưng Phương Nhàn lại dùng những chiêu trò kỳ lạ như mở tiệc cưới kiểu &quot;dòng nước chảy&quot;, liệu pháp trị liệu bằng cá để vực dậy ngành kinh doanh nhà tắm của gia tộc, khiến cô bắt đầu nhìn anh bằng con mắt khác. Đặc biệt, khi gặp nguy hiểm, cô tận mắt chứng kiến Phương Nhàn không ngại nguy hiểm, hy sinh cứu mình, từ đó tình cảm dần nảy sinh.', 'ongoing', '', NULL, NULL, false, '[{"id": "37a7b38b6184a5ebd3c43015aa20709d", "name": "Chính Kịch", "slug": "chinh-kich"}, {"id": "3a17c7283b71fa84e5a8d76fb790ed3e", "name": "Cổ Trang", "slug": "co-trang"}, {"id": "bb2b4b030608ca5984c8dd0770f5b40b", "name": "Tình Cảm", "slug": "tinh-cam"}, {"id": "a7b065b92ad356387ef2e075dee66529", "name": "Tâm Lý", "slug": "tam-ly"}]', '[{"id": "3e075636c731fe0f889c69e0bf82c083", "name": "Trung Quốc", "slug": "trung-quoc"}]', 'Wang Luqing,Samuel Liu,Gao Xiaoyu,Ye Yi,Wang Miaomiao,Zhou Liaoke', 'Đang cập nhật', '2025-05-24 17:36:53.232', 'Tập 20', '24');
INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (1, '10a55f345c206822c64a07d7728aef73', 'thien-kim-nhat-trich', 'Thiên Kim Nhất Trịch', 'All In', 'https://phimimg.com/upload/vod/20250518-1/cec6e99314fa939eae06bc9218cdcff1.jpg', 'https://phimimg.com/upload/vod/20250518-1/1d297f02b8b71d60abc1c78bdc77f264.jpg', 2025, 'series', 'FHD', 'Vietsub', '13 phút/tập', 0, 'Vưu Hạo Uẩn, học trò của một thần bài, bị mất trí nhớ và được gia đình Hách Kim Kim cưu mang. Việc anh vô tình bộc lộ kỹ năng cờ bạc đã cuốn anh vào một loạt rắc rối: Tôn Ức Thiên tìm cách lôi kéo anh nhằm tranh giành quyền lực gia tộc; Hách Kim Kim, vốn căm ghét cờ bạc, lại hiểu lầm anh; trong khi đó, Nặc Nhấtliên tục ép anh tham gia vào các ván độ. Sự xuất hiện của Hạ Trĩ, một góa phụ đang một mình nuôi con, đã vô tình làm bại lộ thân phận thực sự của Vưu Hạo Uẩn, đồng thời dẫn đến việc tìm ra hung thủ của một vụ án mạng nghiêm trọng. Vưu Hạo Uẩn buộc phải vận dụng tài năng cờ bạc của mình để phá giải bế tắc, qua đó vạch trần sự thật đằng sau những mánh khóe lừa đảo tinh vi trong xã hội.', 'ongoing', '', NULL, 'popular_tv', false, '[{"id": "37a7b38b6184a5ebd3c43015aa20709d", "name": "Chính Kịch", "slug": "chinh-kich"}, {"id": "bb2b4b030608ca5984c8dd0770f5b40b", "name": "Tình Cảm", "slug": "tinh-cam"}, {"id": "a7b065b92ad356387ef2e075dee66529", "name": "Tâm Lý", "slug": "tam-ly"}]', '[{"id": "3e075636c731fe0f889c69e0bf82c083", "name": "Trung Quốc", "slug": "trung-quoc"}]', 'Lưu Kiếm Vũ,Minh Duyệt,Lý Tụng Từ,Thượng Na,Vương Lý Đan Ni', 'Khúc Thế Hàng', '2025-05-24 17:36:55.924', 'Tập 16', '26');
INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (83, '92759f3b989bd57b544abacd6cf92d32', 'ngu-uyen-lenh', 'Ngự Uyên Lệnh', 'Love Rewritten', 'https://phimimg.com/upload/vod/20250519-1/1a9f621e4b09507c457e4d412624251d.jpg', 'https://phimimg.com/upload/vod/20250519-1/3cf902d8bb1a366a317acad90c076ec1.jpg', 2025, 'series', 'FHD', 'Vietsub', '14 phút/tập', 0, 'Một vụ ám sát bất ngờ đã khiến Tiêu Cảnh Dương và Lâm Minh Huyên – cặp đôi vốn đã định ly hôn – không chỉ bị hoán đổi linh hồn cho nhau mà còn trùng sinh trở về thời điểm trước khi họ thành hôn. Cả hai buộc phải cùng nhau tìm cách để hoán đổi thân xác trở lại. Ngay khi vừa trở lại thân xác của mình và mọi hiểu lầm tưởng chừng sắp được hóa giải, Tiêu Cảnh Dương lại chọn cách đẩy Lâm Minh Huyên ra xa. Tuy nhiên, cô vẫn phát hiện ra bí mật mà anh cố gắng che giấu. Vì vậy, đứng trước ranh giới sinh tử và giữa lúc các thế lực đen tối đang ngấm ngầm trỗi dậy, lần này, Lâm Minh Huyên quyết tâm sẽ cùng Tiêu Cảnh Dương nắm chặt tay nhau, đối mặt với mọi chuyện.', 'completed', 'https://www.youtube.com/watch?v=_zBscumVoww', NULL, NULL, false, '[{"id": "37a7b38b6184a5ebd3c43015aa20709d", "name": "Chính Kịch", "slug": "chinh-kich"}, {"id": "3a17c7283b71fa84e5a8d76fb790ed3e", "name": "Cổ Trang", "slug": "co-trang"}, {"id": "bb2b4b030608ca5984c8dd0770f5b40b", "name": "Tình Cảm", "slug": "tinh-cam"}, {"id": "a7b065b92ad356387ef2e075dee66529", "name": "Tâm Lý", "slug": "tam-ly"}]', '[{"id": "3e075636c731fe0f889c69e0bf82c083", "name": "Trung Quốc", "slug": "trung-quoc"}]', 'Triệu Việt,Đới Cảnh Diệu,Lý Nhược Gia,Lâm Á Đông', 'Nhậm Đình Lộ', '2025-05-24 17:36:56.815', 'Hoàn Tất (24/24)', '24');
