INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (112, 'c9ce98a38ebb27ccdb0e5083f2cfcde7', 'summer-pockets', 'Summer Pockets', 'Summer Pockets', 'https://phimimg.com/upload/vod/20250423-1/c2541708fdd9f9bd0f4ead5d6e2bf2a0.jpg', 'https://phimimg.com/upload/vod/20250423-1/8fe528b591540f01391de45798506a9c.jpg', 2025, 'hoathinh', 'FHD', 'Vietsub', '24 phút/tập', 0, 'Hairi Takahara không bao giờ mong đợi mùa hè sẽ giống như một giấc mơ. Được gửi đến Torishirojima để sắp xếp đồ đạc của người bà quá cố, anh đã gặp biển cả mênh mông, nỗi nhớ yên bình và những cô gái bí ẩn, mỗi người đều theo đuổi một thứ gì đó ngoài tầm với. Khi anh ổn định cuộc sống trên đảo, những ký ức đã mất bắt đầu xuất hiện và anh tìm thấy thứ mà anh chưa từng biết mình đã mất.', 'ongoing', 'https://www.youtube.com/watch?v=2u-RIQst_N4', NULL, NULL, false, '[{"id": "37a7b38b6184a5ebd3c43015aa20709d", "name": "Chính Kịch", "slug": "chinh-kich"}, {"id": "bb2b4b030608ca5984c8dd0770f5b40b", "name": "Tình Cảm", "slug": "tinh-cam"}, {"id": "a7b065b92ad356387ef2e075dee66529", "name": "Tâm Lý", "slug": "tam-ly"}]', '[{"id": "d4097fbffa8f7149a61281437171eb83", "name": "Nhật Bản", "slug": "nhat-ban"}]', 'Shoya Chiba,Konomi Kohara,Natsumi Takamori,Nene Hieda,Emiri Iwai,Saku Ichimiya,Sahomi Koyama,Aimi Tanaka,Megumi Takamoto,Minoru Shiraishi,Konomi Suzuki,Kentaro Kumagai,Youhei Hamada', 'Đang cập nhật', '2025-05-24 17:38:47.916', 'Tập 6', '12');
INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (113, 'ccd9f73a3bb415a32b0708b0821761c4', 'guilty-gear-strive-dual-rulers', 'Guilty Gear Strive: Dual Rulers', 'Guilty Gear Strive: Dual Rulers', 'https://phimimg.com/upload/vod/20250420-1/fa83475e18c570ff30784362df5e3b53.jpg', 'https://phimimg.com/upload/vod/20250420-1/d01fcd43df4b3568e35465faff5f83af.jpg', 2025, 'hoathinh', 'FHD', 'Vietsub', '24 phút/tập', 0, 'Sau khi các lĩnh vực khoa học và công nghệ từng rất hùng mạnh sụp đổ, một nguồn năng lượng mới gọi là ma thuật trở thành nền tảng cho thời đại hiện đại. Nhân loại đã tạo ra các vũ khí sinh học bị cấm gọi là Gear. Những Gear này cuối cùng nổi dậy chống lại loài người trong một cuộc nổi loạn. Dù nhân loại đã chiến thắng trong cuộc chiến sống còn được gọi là Thập tự chinh (Crusades), nhưng tổn thất của họ lớn đến mức nhiều thập kỷ sau đó, những vết thương lòng vẫn chưa thể hoàn toàn lành lại. Sin Kiske, đứa trẻ sinh ra từ một con người và một Gear, đang trên đường tới dự lễ cưới của cha mình là Ky và mẹ là Dizzy. Hôn lễ này đã phá vỡ điều cấm kỵ lớn nhất: sự kết hợp giữa con người và Gear. Dù thế giới đang trong hòa bình, nhưng phải mất rất nhiều năm mới có thể tổ chức được lễ cưới này. Mặc dù có những cảm xúc phức tạp vây quanh, buổi lễ vẫn diễn ra với nhiều lời chúc phúc. Đột nhiên, một cô gái bí ẩn xuất hiện... Một đứa trẻ mang trong mình dòng máu Gear định mệnh, và cô gái bí ẩn có lòng căm ghét sâu sắc đối với Gear. Cuộc gặp gỡ của họ sẽ làm rung chuyển cả thế giới.', 'ongoing', 'https://www.youtube.com/watch?v=CcMgvwfQPHY', NULL, NULL, false, '[{"id": "0bcf4077916678de9b48c89221fcf8ae", "name": "Khoa Học", "slug": "khoa-hoc"}, {"id": "68564911f00849030f9c9c144ea1b931", "name": "Viễn Tưởng", "slug": "vien-tuong"}, {"id"