INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (115, '7b5f37cf6529d139d0735e31fa4baae4', 'trinh-sat-hinh-su-12', 'Trinh Sát Hình Sự 12', 'D.I.D 12', 'https://phimimg.com/upload/vod/20250522-1/9d758bbaa21d17621702a23c8604cbe9.jpg', 'https://phimimg.com/upload/vod/20250522-1/b3698cc3c9880bdcec27e6f1c737cb69.jpg', 2025, 'series', 'FHD', 'Lồng Tiếng', '43 phút/tập', 0, 'Một loạt án mạng liên hoàn, đã tập hợp lại Đinh Lạc Phong (Lâm Bảo Di) sỹ quan cảnh sát tổ trọng án và thanh tra cấp cao Hình Khải (Trương Chấn Lãng). Trong quá trình truy bắt hung thủ, Hình Khải buộc phải tìm sự trợ giúp của Dương Chỉ Phi (Phó Gia Lê), một nhà tâm lý học đồng thời là bạn gái cũ của Khải. Ba người nghiên cứu tỉ mỉ, phát hiện hung thủ là một tổ chức sát thủ, bằng cách cung cấp bố cục “Tân Bảy Mối Tội” qua trang web thần bí, xử tử người nào đã phạm phải “Tân Bảy Mối Tội”; và cũng khẳng định hung thủ có mối liên quan với tập đoàn Hy Nghiệp đang nắm giữ mạch máu kinh tế của thành phố này... Hình Khải không bao giờ ngờ rằng, cái mà chờ đón anh còn có một thời quá khứ anh rất muốn quên nhưng không được, đó làm cho anh ta khốn khó. Chị ruột của Lạc Phong là Đinh Thiếu Vân (Trần Pháp Dung) từ nước ngoài về lai ôm ấp một âm mưu đen tối, làm cho công việc điều tra thêm rắc rối...', 'ongoing', 'https://www.youtube.com/watch?v=UbUJbFsOtqs', NULL, NULL, false, '[{"id": "7a035ac0b37f5854f0f6979260899c90", "name": "Hình Sự", "slug": "hinh-su"}, {"id": "2fb53017b3be83cd754a08adab3e916c", "name": "Bí Ẩn", "slug": "bi-an"}, {"id": "9822be111d2ccc29c7172c78b8af8ff5", "name": "Hành Động", "slug": "hanh-dong"}, {"id": "37a7b38b6184a5ebd3c43015aa20709d", "name": "Chính Kịch", "slug": "chinh-kich"}]', '[{"id": "a0cd53c9875b96640ac264ca81996f9f", "name": "Hồng Kông", "