INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (118, 'd92ea9b81f514a1f8c2330f1b579bf21', 'chieu-chieu-nhu-nguyen', 'Chiêu Chiêu Như Nguyện', 'My Unpredictable Lover', 'https://phimimg.com/upload/vod/20250505-1/44747d212f842a17562797c01b814e38.jpg', 'https://phimimg.com/upload/vod/20250505-1/8243a6eb3f3056a6f93bc85108aad2bd.jpg', 2025, 'series', 'FHD', 'Vietsub', '8 phút/tập', 0, 'Lấy bối cảnh vào năm thứ 23 của triều đại Đại Tề, bộ phim kể về Tô Tiểu Tiên, một cô gái xuất thân bình dân nhưng sở hữu tài năng nấu nướng xuất sắc. Trong một lần đi hái nguyên liệu, cô vô tình lạc vào khu săn bắn hoàng gia và cứu sống Tam hoàng tử Triệu Hàn khỏi một cuộc ám sát. Để bảo vệ cô khỏi những rắc rối chính trị, Triệu Hàn đưa cô về phủ dưới danh nghĩa đầu bếp tạm thời. Tuy nhiên, Tô Tiểu Tiên không muốn bị giam cầm và luôn tìm cách rời khỏi phủ để theo đuổi ước mơ mở quán ăn riêng. Sau nhiều lần thất bại trong việc trốn thoát, cô quyết định nấu một bữa ăn tệ hại để bị đuổi đi. Nhưng món &quot;rượu hoa đào&quot; của cô lại khiến Triệu Hàn say mê. Trong cơn say, Triệu Hàn phát tác chứng bệnh kỳ lạ, khiến anh xuất hiện một nhân cách thứ hai – Triệu Nhiên. Từ đây, mối quan hệ giữa Tô Tiểu Tiên, Triệu Hàn và Triệu Nhiên trở nên phức tạp, đồng thời hé lộ những bí mật liên quan đến một loại độc dược thần bí từ phương Nam.', 'ongoing', 'https://www.youtube.com/watch?v=Al2RXD4Ly0Y', NULL, NULL, false, '[{"id": "37a7b38b6184a5ebd3c43015aa20709d", "name": "Chính Kịch", "slug": "chinh-kich"}, {"id": "3a17c7283b71fa84e5a8d76fb790ed3e", "name": "Cổ Trang", "slug": "co-trang"}, {"id": "bb2b4b030608ca5984c8dd0770f5b40b", "name": "Tình Cảm", "slug": "tinh-cam"}, {"id": "a7b065b92ad356387ef2e075dee66529", "name": "Tâm Lý", "slug": "tam-ly"}, {"id": "66c78b