INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (124, 'b71faf915b91184a5c4dc56c7d6262da', 'bang-thuong-giai-te', 'Bảng Thượng Giai Tế', 'Serendipity', 'https://phimimg.com/upload/vod/20250425-1/a8a17267760c77de2cb08e2e043ab06e.jpg', 'https://phimimg.com/upload/vod/20250425-1/8bcc977603884adde71ce30d80d1f3d1.jpg', 2025, 'series', 'FHD', 'Vietsub', '45 phút/tập', 0, 'Giản Minh Thư, thiên kim Giản phủ ở Giang Ninh, vô tình rơi xuống vực và mất trí nhớ, nhưng cô đã thoát khỏi thảm kịch gia đình bị xóa sổ và được người cô yêu là Lục Thường cứu thoát. Hai người cùng nhau đến kinh đô để dự thi. Cô luôn nghi ngờ về danh tính của mình, bước vào ngôi nhà để điều tra và giải quyết bí ẩn, và bất ngờ có được manh mối về trải nghiệm sống của mình. Tuy nhiên, cuộc điều tra của Giản Minh Thư dần rơi vào thế khó. Cô nghĩ rằng mình đang bước đi một mình trong nguy hiểm, nhưng thực ra Lục Thường đang âm thầm bảo vệ cô. Sự dịu dàng và tình cảm của Lục Thường càng làm tan chảy trái tim Minh Thư. Hai người đã cùng nhau trải qua nhiều nguy hiểm, cùng nhau chống chọi với mưa gió. Cuối cùng, họ đã xác nhận tình cảm của mình và dành phần đời còn lại bên nhau.', 'completed', 'https://www.youtube.com/watch?v=eFRZRjKUxAw', NULL, NULL, false, '[{"id": "37a7b38b6184a5ebd3c43015aa20709d", "name": "Chính Kịch", "slug": "chinh-kich"}, {"id": "3a17c7283b71fa84e5a8d76fb790ed3e", "name": "Cổ Trang", "slug": "co-trang"}, {"id": "bb2b4b030608ca5984c8dd0770f5b40b", "name": "Tình Cảm", "slug": "tinh-cam"}, {"id": "a7b065b92ad356387ef2e075dee66529", "name": "Tâm Lý", "slug": "tam-ly"}]', '[{"id": "92aa3c93de523a414a520399bb6a4304", "name": "Trung Quốc", "slug": "trung-quoc"}]', 'Vương Tử Kỳ,Lư Dực Hiểu ,Vương Hoằng Nghị,Khổng Tuyết Nhi,Hạc Thu,Dương Sĩ Trạch,Uông Tị