INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (140, 'bdbe575de5ecbc20be758c6f4e105da6', 'bonhoeffer-muc-su-gian-diep-ke-am-sat', 'Bonhoeffer: Mục Sư, Gián Điệp, Kẻ Ám Sát', 'Bonhoeffer: Pastor. Spy. Assassin', 'https://phimimg.com/upload/vod/20250523-1/4e54c5bb56dd70e332f3c1573532664d.jpg', 'https://phimimg.com/upload/vod/20250523-1/e5292501444b5ab8b66a94793fc8267c.jpg', 2024, 'single', 'FHD', 'Vietsub', '132 phút', 0, 'Phim kể về Dietrich Bonhoeffer, một mục sư và nhà thần học người Đức, người đã dũng cảm chống lại chế độ phát xít Đức dưới thời Hitler. Sau khi chứng kiến sự tàn bạo của Đức Quốc xã và sự thoái hóa của các nhà thờ quốc doanh, ông quyết định tham gia vào phong trào kháng chiến. Không chỉ giảng đạo, Bonhoeffer còn trở thành gián điệp và âm thầm hỗ trợ các kế hoạch ám sát Hitler.Trong khi đào tạo các mục sư ngầm và giúp người Do Thái trốn thoát, Bonhoeffer bị bắt vào năm 1943 và cuối cùng bị xử tử năm 1945 tại trại tập trung Flossenbürg, chỉ vài tuần trước khi chiến tranh kết thúc.', 'completed', 'https://www.youtube.com/watch?v=WZM90izJ8sI', NULL, NULL, false, '[{"id": "f8ec3e9b77c509fdf64f0c387119b916", "name": "Lịch Sử", "slug": "lich-su"}, {"id": "37a7b38b6184a5ebd3c43015aa20709d", "name": "Chính Kịch", "slug": "chinh-kich"}, {"id": "1bae5183d681b7649f9bf349177f7123", "name": "Chiến Tranh", "slug": "chien-tranh"}]', '[{"id": "93e1c1318f7b84817ab855208973039b", "name": "Bỉ", "slug": "bi"}, {"id": "06e415f918c577f07328a52e24f75d43", "name": "Ireland", "slug": "ireland"}]', 'Jonas Dassler,Phileas Heyblom,August Diehl,David Jonsson,Moritz Bleibtreu,Nadine Heidenreich,Clarke Peters,Flula Borg,Lisa Hofer,Arthur Riordan,Luise Landau,William Robinson,James Flynn,Greg Kolpakchi,Simon Licht,Mark Wingett,Evan Hart,Vi'