INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (145, '383a2a1e8e4bf855f4149671967161bb', 'ho-bao-tam-tung', 'Hộ Bảo Tầm Tung', 'The Lost National Treasure', 'https://phimimg.com/upload/vod/20250521-1/d2d70ea36d966acddd127939dec8b076.jpg', 'https://phimimg.com/upload/vod/20250521-1/8167bd3c80d7944f1e3e68bf52ba7ea6.jpg', 2025, 'series', 'FHD', 'Vietsub', '45 phút/tập', 0, 'Phương Khôn, nghiên cứu sinh ngành Khảo cổ tại Đại học Tần Xuyên, trong quá trình hỗ trợ truy tìm cổ vật thất lạc ở nước ngoài, phát hiện một lăng mộ tại làng Ấn, Bình Lộc Nguyên có thể chính là lăng hoàng đế cấp cao chưa được biết đến. Thầy hướng dẫn Trẩn Mậu Xương dẫn cô đến thực địa khảo sát tại Bình Lộc Nguyên, nhưng không ngờ đã hy sinh nơi đây. Mang theo di nguyện của thầy, Phương Khôn trưởng thành và trở thành thành viên của đội khảo cổ. Việc phát hiện lăng mộ bí ẩn ở làng Ấn nhanh chóng thu hút sự quan tâm của giới khảo cổ. Phương Khôn được mời tham gia đợt khảo sát mới và tại đây cô tái ngộ Lạc Thanh, người yêu cũ đã lên chức đội trưởng khảo cổ. Khi Phương Khôn khơi lại những giả thuyết năm xưa, không chỉ gây tranh cãi trong giới học thuật, mà còn đánh thức tham vọng của các bọn tội phạm trộm bán cổ vật. Phương Khôn, Lạc Thanh cùng Đội trưởng Đội Trinh sát Cổ vật Cục Công an Tề Đại Thương — những chiến sĩ bảo vệ di sản văn hóa tuyến đầu — đã huy động người dân địa phương tham gia bảo vệ di tích, phối hợp đấu trí đấu dũng với băng nhóm tội phạm do Mộc Kiến Huy cầm đầu, bảo vệ vững chắc di sản quý báu của Bình Lộc Nguyên.', 'ongoing', 'https://www.youtube.com/watch?v=yVlyYJsOqNg', NULL, NULL, false, '[{"id": "37a7b38b6184a5ebd3c43015aa20709d", "name": "Chính Kịch", "slug": "chinh-kich"}, {"id": "2fb53017b3be83cd754a08adab3e916c", "name": "Bí Ẩn", "slu