INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (179, 'e82ba7292d1c4fbfbf1933dc51f62e60', 'hien-ngu', 'Hiến Ngư', 'When Destiny Brings The Demon', 'https://phimimg.com/upload/vod/20250816-1/0cf65fc65804a01c9c35155750e7672f.jpg', 'https://phimimg.com/upload/vod/20250816-1/9d2f4ee7f4f131f23a8de09669648ca2.jpg', 2025, 'series', 'FHD', 'Vietsub + Thuyết Minh', '45 phút/tập', 0, 'Bộ phim xoay quanh Trâu Nhạn – một “nô lệ công sở” điển hình thời hiện đại – vô tình xuyên không đến thế giới tu tiên, trở thành Liêu Đình Nhạn, đệ tử của Thanh Cốc Thiên, đồng thời bị tuyển chọn vào hàng ngũ một trăm đệ tử hầu hạ sư tổ Tư Mã Tiêu. Bề ngoài, Tư Mã Tiêu được cho là đang bế quan ở núi Tam Thánh, nhưng thực chất hắn đã bị phong ấn suốt 500 năm, hình thành nên tính cách u ám, lầm lì, cáu kỉnh dễ nổi nóng. Hắn vô cùng căm hận Tiên Phủ Canh Thần và luôn nuôi ý định trả thù. Liêu Đình Nhạn thực chất là thân phận của một thích khách do Ma Vực cài vào, nhưng Trâu Nhạn không hề biết &quot;bản thân&quot; còn che giấu thiết lập này nên ngay từ lần đầu chạm mặt, cô suýt nữa đã mất mạng dưới tay Tư Mã Tiêu. Sau thời gian tiếp xúc, nhờ năng lực “lời thề nói thật”, Tư Mã Tiêu dần nhận ra cô không hề có ý định ám sát hay lợi dụng mình. Thái độ này khiến hắn cảm thấy tò mò, nảy sinh hứng thú, từ đó bắt đầu chuyện tình dây dưa nhiều kiếp giữa ma vương phản diện và cô gái lười hệ chữa lành.', 'ongoing', 'https://www.youtube.com/watch?v=CHAWRCiXzd0', NULL, NULL, false, '[{"id": "37a7b38b6184a5ebd3c43015aa20709d", "name": "Chính Kịch", "slug": "chinh-kich"}, {"id": "3a17c7283b71fa84e5a8d76fb790ed3e", "name": "Cổ Trang", "slug": "co-trang"}, {"id": "bb2b4b030608ca5984c8dd0770f5b40b", "name": "Tình Cảm", "slug": "tinh-cam"}, {"id": "a7b065b92ad356387ef2e075dee66