INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (183, '6df811b75fbf2f0dc32a60ece213f1a3', 'truyen-thuyet-phuong-hoang-phuong-dich', 'Truyền Thuyết Phượng Hoàng (Phượng Dịch)', 'Legend Of The Phoenix', 'https://phimimg.com/upload/vod/20250817-1/b885f658ef861d6724d5bed0ab0f8192.jpg', 'https://phimimg.com/upload/vod/20250817-1/97324ae502143cc80c5e19e52b20b281.jpg', 2019, 'series', 'FHD', 'Vietsub + Lồng Tiếng', '40 phút/tập', 0, 'Vào thời Đông Lương, có nàng Ngưng Chi xinh đẹp lương thiện. Một lần nàng theo đoàn kịch nhập cung biểu diễn chúc mừng Thọ Yến của Hoàng Thái Hậu, không ngờ rằng lại bị trưởng công chúa hãm hại, may mắn thay được đệ nhất mưu sĩ Ngụy Quảng kịp thời ra tay giúp đỡ, Ngưng Chi từ đây đối với chàng tương tư mãi không quên. Hai năm sau, Ngưng Chi ngoài ý muốn tiến cung làm cung nữ hầu cận của Hoàng Hậu. Với sự thông minh, lòng nhân hậu và sự dũng cảm Diệp Ngưng Chi đã chiến đấu vì tầng lớp nghèo khổ, chống lại cường quyền và trở thành giai thoại trong Hoàng Cung, khiến chính hoàng hậu cũng phải cảm động với tấm chân tình của nàng, đồng thời cũng hấp dẫn sự chú ý của vị Hoàng Đế cao cao tại thượng, nàng từ đó lâm vào tình thế nguy nan, nhất cử nhất động trong hậu cung đều trở nên vô cùng khó khăn. Cùng lúc ấy, Trưởng công chúa bắt đầu củng cố quyền lực, Diệp Ngưng Chi vô tình bị cuốn vào vòng xoay tranh quyền đoạt thế. May mắn thay Ngụy Quảng vẫn luôn kề bên nàng, cùng nàng vượt qua trùng trùng điệp điệp khó khăn, đẩy lùi Quốc nạn. Từ một cung nữ thân phận thấp hèn, Diệp Ngưng Chi đã trở thành Hiền Phi trí tuệ nhân hậu, danh tiếng lưu truyền đến ngàn đời sau. Cuối cùng nàng trở lại cuộc sống thường dân sống một cuộc đời viên mãn với tình yêu chân thành.', 'completed', 'https://www.youtube.com/watch?v=nC1vzfeup