INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (293, 'ea8f123bcd0751b651e0944774d790e0', 'ta-la-dao-tong', 'Ta Là Đao Tông', 'I Am The Blade', 'https://phimimg.com/upload/vod/20250725-1/8e383a9e0066ee11ffc1d8aa745cb57a.jpg', 'https://phimimg.com/upload/vod/20250725-1/98fde2db0a6d463590924d0c401ccfe3.jpg', 2025, 'hoathinh', 'FHD', 'Vietsub + Thuyết Minh', '20 phút/tập', 0, 'Trong một âm mưu giang hồ, Tông chủ Đao Tông – võ lâm chí tôn – bị sát hại, các thủ lĩnh bang phái lớn hoặc bỏ mạng, hoặc rơi vào vòng lao ngục, khiến giang hồ rơi vào hỗn loạn. Để tái lập trật tự võ lâm, Yến Ký Vũ – các chủ Trường Phong Các – cử sứ giả đến các môn phái, mưu đồ xây dựng một võ lâm “chính khí” do hắn đứng đầu. Trong lúc đó, Dương Nhận – một kẻ vừa bước chân vào giang hồ – nhận ủy thác đưa đi bức thư do Đao Tông để lại. Dọc đường, kẻ địch rình rập, hiểm nguy trùng trùng, nhưng anh nhờ nhiệt huyết và nghĩa hiệp mà không ngừng vượt qua bản thân, đánh bại đối thủ, võ công tiến bộ vượt bậc, đồng thời kết giao được nhiều người hào hiệp chính nghĩa. Giang hồ nên là nơi như thế nào? Dương Nhận không có câu trả lời, nhưng anh biết chắc chắn đó không phải là thứ giang hồ “chính khí” mà Yến Ký Vũ tạo ra. Dương Nhận dựng lá cờ “Thừa Phong Bang”, nhanh chóng quy tụ những người phản đối Yến Ký Vũ. Đại chiến sắp nổ ra, không ai có thể đứng ngoài. Sau cái chết của Đao Tông, giang hồ đã chia cắt bấy lâu, nay sẽ được khâu lại với sự xuất hiện của tân Đao Tông.', 'ongoing', 'https://www.youtube.com/watch?v=23Xhy8blZ30', NULL, NULL, false, '[{"id": "9822be111d2ccc29c7172c78b8af8ff5", "name": "Hành Động", "slug": "hanh-dong"}, {"id": "66c78b23908113d478d8d85390a244b4", "name": "Phiêu Lưu", "slug": "phieu-luu"}, {"id": "0bcf4077916678de9b48c89221fcf8ae", "name":