INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (295, '3aef625160d85a9fc3b52f3be4474ed5', 'de-linh-yeu-mac-thuy-linh-lung', 'Đế Linh Yêu Mặc Thuỷ Linh Lung', 'Divine Love Deep Blue', 'https://phimimg.com/upload/vod/20250802-1/30f12e6c13f801fd2146913879f4409b.jpg', 'https://phimimg.com/upload/vod/20250802-1/4f7f04c688a3d3e2bfb6bde6eb796296.jpg', 2025, 'hoathinh', 'FHD', 'Vietsub + Thuyết Minh', '14 phút/tập', 0, 'Câu chuyện bắt đầu từ thuở xa xưa, khi mà vạn vật trên đời này đều sở hữu những nguồn sức mạnh và ý thức của riêng mình, dù là nhỏ nhoi và ít ỏi, nhưng gộp chung lại thì chúng tạo ra những thực thể với sức mạnh và ý thức vô cùng to lớn, có thể gọi đó là những tiên tử. Nhưng tiên tử cũng đang bị đe dọa bởi sự cuồng vọng của con người, và thêm với việc lớp phong ấn của một thứ sinh mạnh kỳ bí đang dần bị phá hỏng, các tiên tử dần chịu cảnh thương tổn không ít. Trong khi đó thì một nữ nhân lại vô tình gặp được một tiên tử, họ nảy sinh rất nhiều thứ tình cảm với nhau, mà ta có thể gọi đó chính là tình yêu, nhưng rào cản giữa tiên và người đã dần ngăn cách họ. Vô cùng đau buồn vì sự thực nghiệt ngã ấy, nữ nhân kia đã quyết tâm lên đường để chinh phục những thứ sức mạnh mới cho bản thân, tìm ra bản ngã cho riêng mình, bởi cô luôn có một chấp niệm rằng con người cũng là một phần của tự nhiên, cũng như việc cô và tiên tử vẫn có thể tìm ra cách thức để chung sống với thứ tình cảm thiêng liêng của mình.', 'ongoing', '', NULL, NULL, false, '[{"id": "37a7b38b6184a5ebd3c43015aa20709d", "name": "Chính Kịch", "slug": "chinh-kich"}, {"id": "66c78b23908113d478d8d85390a244b4", "name": "Phiêu Lưu", "slug": "phieu-luu"}]', '[{"id": "92aa3c93de523a414a520399bb6a4304", "name": "Trung Quốc", "slug": "trung-quoc"}]', 'Đang cập nhật', 'Đang 