INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (296, '3400f321cb9fe8a7c2efa0e1edb2eaf5', 'tieu-tien-chi-dao-xiao-xian-zhi-yao', 'Tiểu Tiên Chi Dao (Xiao Xian Zhi Yao)', 'Little Fairy Yao', 'https://phimimg.com/upload/vod/20250804-1/f1e285897d82bc7b5ee42f88042aa898.jpg', 'https://phimimg.com/upload/vod/20250804-1/6c567708ab7a3a6e1c26a23ef5b5f705.jpg', 2025, 'hoathinh', 'FHD', 'Vietsub', '10 phút/tập', 0, 'Giữa cơn gió lạnh thổi qua chín tầng trời, vị nữ chúa của Ẩn tộc, kẻ khiến tam giới kinh hãi được biết đến với cái tên Hòa Uyển đã bất ngờ dẫn quân xâm lấn Thiên Đình. Không hề nao núng, Nam Trạch, vị thượng thần uy dũng, đứng lên nghênh chiến, được trợ giúp bởi Ngu Chi Dao, một tiên nữ chăm sóc ngọc lan giản dị, liều mình bảo vệ ngài. Giữa thời khắc định mệnh ngàn cân treo sợ bún đồng sinh cộng tử của Nam Trạch và Hòa Uyển, Chi Dao đã chẳng may chết đi nhưng đã được gửi trở lại, và cũng kỳ diệu thay, sự tái sinh này đã đưa cô cộng sinh trong nhục thể của Hoà Uyển. Sức mạnh Ẩn tộc trong cô gần như tan biến. Cùng lúc đó thì Thính Chiêu, một ẩn vệ từ vùng biển Tây Hải đã xuất hiện để hỗ trợ nữ chúa của mình lấy lại thần lực. Từ đó, Chi Dao sống hai cuộc đời: trước bá tánh, cô giữ vẻ uy nghiêm của bậc tôn chủ, nhưng bên người thân thiết, cô hóa thành thiếu nữ nhu mì, nết na.', 'ongoing', 'https://www.youtube.com/watch?v=mIK-lQnMOJU', NULL, NULL, false, '[{"id": "9822be111d2ccc29c7172c78b8af8ff5", "name": "Hành Động", "slug": "hanh-dong"}, {"id": "66c78b23908113d478d8d85390a244b4", "name": "Phiêu Lưu", "slug": "phieu-luu"}, {"id": "68564911f00849030f9c9c144ea1b931", "name": "Viễn Tưởng", "slug": "vien-tuong"}, {"id": "a7b065b92ad356387ef2e075dee66529", "name": "Tâm Lý", "slug": "tam-ly"}, {"id": "37a7b38b6184a5ebd3c43015aa20709d