INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (319, '6d061501b6395b30cfb6aaa256e138af', 'kiem-sac-hoa-hong', 'Kiếm Sắc: Hoa Hồng', 'Sword Rose', 'https://phimimg.com/upload/vod/20250729-1/f8ea3feae674f59b4898fc086ee00722.jpg', 'https://phimimg.com/upload/vod/20250729-1/07e040ca9d9aebb91e7a6027822e9525.jpg', 2025, 'series', 'FHD', 'Vietsub', '45 phút/tập', 0, 'Trong một lần vô tình can thiệp vào một vụ việc khẩn cấp, Đặng Nghiên đã góp công giải cứu một cô gái bị bắt cóc. Tuy nhiên, hành động này lại vô tình làm gián đoạn kế hoạch truy bắt tội phạm mà Phòng Phòng, chống Mua bán người đã dày công chuẩn bị. Sau sự cố đó, cô bất ngờ được bổ nhiệm làm Trưởng phòng mới của đơn vị này. Quyết định bổ nhiệm khiến Thường Duệ – một cảnh sát từng nằm vùng trong các chuyên án chống ma túy – cảm thấy bất mãn. Trong mắt anh, Đặng Nghiên là một Trưởng phòng “nhảy dù”, thiếu kinh nghiệm thực chiến. Thế nhưng, sau khi nhậm chức, Đặng Nghiên đã nhanh chóng khẳng định năng lực chuyên môn, tham gia tích cực vào công tác điều tra phá án và đóng vai trò chủ chốt trong nhiều vụ việc phức tạp. Nhờ vậy, cô dần giành được sự tin tưởng từ các đồng nghiệp và từng bước thay đổi hoàn toàn cách nhìn của Thường Duệ qua quá trình hợp tác. Về phía Thường Duệ, trong quá trình điều tra, anh nhiều lần bị giằng xé giữa tình cảm cá nhân và trách nhiệm nghề nghiệp, khiến bản thân rơi vào bế tắc. Tuy nhiên, nhờ sự động viên từ những người xung quanh, anh dần thức tỉnh và nhận ra trọng trách thiêng liêng của một chiến sĩ chống buôn người, từ đó quyết tâm tiếp tục cống hiến cho sứ mệnh cao cả này. Riêng Đặng Nghiên, cô luôn mang trong mình ngọn lửa căm phẫn với tội ác buôn người – một cảm xúc xuất phát từ nỗi đau khi người bạn thân thuở nhỏ mất tích. Chính điều đó đã thôi thúc cô không ngừng đào sâu điều tra, ứng dụng các công nghệ hiện đại để phá giải hàng loạt vụ án tồn đọng. Khi từng vụ án lần lượt được làm sáng tỏ, một mạng lưới buôn người khổng lồ dần lộ diện. Trong mỗi chiến dịch, Đặng Nghiên ngày càng thấm thía hơn trách nhiệm nặng nề mà cô đang gánh vác. Đối mặt với tình hình phức tạp và những biến cố bất ngờ, dù bản thân rơi vào nguy hiểm, thậm chí có nguy cơ bị hãm hại, cô vẫn kiên trì theo đuổi sự thật đến cùng. Sau khi được minh oan, Đặng Nghiên đã dẫn dắt cả đội bước vào cuộc đối đầu quyết liệt với thế lực ngầm đứng sau những đường dây buôn người.', 'completed', 'https://www.youtube.com/watch?v=PhCT8sYrOpw', NULL, NULL, false, '[{"id": "7a035ac0b37f5854f0f6979260899c90", "name": "Hình Sự", "slug": "hinh-su"}, {"id": "2fb53017b3be83cd754a08adab3e916c", "name": "Bí Ẩn", "slug": "bi-an"}, {"id": "a7b065b92ad356387ef2e075dee66529", "name": "Tâm Lý", "slug": "tam-ly"}, {"id": "37a7b38b6184a5ebd3c43015aa20709d", "name": "Chính Kịch", "slug": "chinh-kich"}]', '[{"id": "3e075636c731fe0f889c69e0bf82c083", "name": "Trung Quốc", "slug": "trung-quoc"}]', 'Địch Lệ Nhiệt Ba,Kim Thế Giai,Vưu Dũng Trí,Lý Trí,Vương Nghệ Triết,Cao Cơ Tài,Hạ Tinh', 'Tô Vạn Xung', '2025-08-17 08:41:43.896', 'Hoàn Tất (32/32)', '32');
INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (297, 'b10d25cbe07aeb3bbf6ec350cec76fcc', 'phieu-mieu-kiem-tien-truyen', 'Phiêu Miểu Kiếm Tiên Truyện', 'Legend Of The Misty Sword Immortal', 'https://phimimg.com/upload/vod/20250811-1/8d69b8d19589c20002a676d17e5cc071.jpg', 'https://phimimg.com/upload/vod/20250811-1/631a67d6788ab55ec6765ed359a96254.jpg', 2025, 'hoathinh', 'FHD', 'Vietsub + Thuyết Minh', '12 phút/tập', 0, 'Câu chuyện được bắt đầu từ anh chàng có tên Đông Phương Thiếu Hồng, một anh chàng hiện lên trong mắt của mọi người với những sự giễu cợt hết lần này đến lần khác, nhưng thật sự thì anh ấy lại đáng mến hơn là đáng cười, bởi đôi khi chỉ thiếu may mắn một chút thôi, hoặc có chăng là may mắn quá, hoặc cũng là may mắn chưa đủ nên rơi vào những tình huống cười ra nước mắt, chứ nếu mà đỏ thì quên đi nhé. Số là mang trong mình tới hai loại thể chất của đại đạo, nhưng chính 2 thứ ấy lại bài xích lẫn nhau nên Đông Phương Thiếu Hồng không thể mở ra linh thể cho việc tu luyện bản thân. Đã đen thì chớ, cha của anh chàng lại bị bọn Tiên Minh bắt đi vì ông ấy biết được nhiều bí mật trong việc đi vào Thương Ngọc Tiên Phủ, nơi mà ẩn chứa rất nhiều điều bí mật khác mà bao người vẫn luôn tò mò. Chưa hết, bọn Tiên Minh và những gia tộc khác lại đang cố chèn ép để đưa gia tộc của anh đến mức diệt vong. Uất hận vì tình cảnh ngặt nghèo vậy, Thiếu Hồng đã dùng máu của mình để tạo nên sức mạnh từ Tiên Nguyên Đạo Thạch, đánh lui được bọn Tiên Minh và các gia tộc khác, bảo vệ cho gia tộc mình tạm thời an toàn, nhưng cha của anh thì đã bị bọn khốn Tiên Minh mang đi rồi, buộc anh phải tìm cách để cứu thoát ông ấy. Một vị cố nhân của ông ấy là Quý Dận đã cứu giúp anh, đưa anh đến một miếu tông của ông để tu luyện, phát triển sức mạnh, bởi đó là cách duy nhất anh có thể làm để cứu cha mình.', 'ongoing', 'https://www.youtube.com/watch?v=jO_RAEmKFr8', NULL, NULL, false, '[{"id": "37a7b38b6184a5ebd3c43015aa20709d", "name": "Chính Kịch", "slug": "chinh-kich"}, {"id": "66c78b23908113d478d8d85390a244b4", "name": "Phiêu Lưu", "slug": "phieu-luu"}]', '[{"id": "3e075636c731fe0f889c69e0bf82c083", "name": "Trung Quốc", "slug": "trung-quoc"}]', 'Đang cập nhật', 'Đang cập nhật', '2025-08-17 08:41:22.355', 'Tập 7', '12');
