INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (324, 'c7a2af589d255231c76944fe4a45a500', 'chuyen-tinh-nang-anna-karenina', 'Chuyện Tình Nàng Anna Karenina', 'Anna Karenina', 'https://phimimg.com/upload/vod/20250814-1/e784738ce3500ce7794dae841ecb7bb6.jpg', 'https://phimimg.com/upload/vod/20250814-1/fe9fdb6894b56b2c55de9fcb053722de.jpg', 2012, 'single', 'FHD', 'Vietsub', '130 phút', 0, 'Dù ở đâu vào thời kỳ nữ quyền chưa lên ngôi, rất ít phụ nữ có được hạnh phúc trong hôn nhân. Trong bối cảnh châu Âu thế kỷ XIX, Anna cũng chịu số phận chua xót như vậy. Đang trong độ tuổi thanh xuân phơi phới, nàng phải kết hôn với người đàn ông hơn mình rất nhiều tuổi, một người mình không hề yêu, một người cứng nhắc và chẳng hề lãng mạn. Cuộc hôn nhân ấy được sắp xếp bởi người cô ham tiền và địa vị, không màng đến sự chấp thuận của Anna. Sánh đôi bên chồng, bá tước Karenin, người cống hiến cả đời mình cho lý tưởng nước Nga, Anna trở thành hình mẫu phu nhân được ao ước trong giới thượng lưu. Dù luôn xuất hiện xinh đẹp và rạng rỡ, trở thành trung tâm của mọi sự chú ý nhưng bên ngoài càng đẹp đẽ, lấp lánh bao nhiêu, tâm hồn nàng lại càng u uất và buồn khổ bấy nhiêu. Còn gì đau đớn hơn việc chung sống với người mình không yêu? Cho đến khi Anna gặp Vronsky và khiêu vũ với chàng, ngọn lửa nhiệt thành trong trái tim nàng mới thực sự bùng cháy. Lần đầu tiên sau 8 năm trời đằng đẵng giam cầm tuổi trẻ bên chồng, trái tim nàng xao động, không thể khước từ tiếng gọi của tình yêu.', 'completed', 'https://www.youtube.com/watch?v=RxtsC_LZ9eI', NULL, NULL, false, '[{"id": "37a7b38b6184a5ebd3c43015aa20709d", "name": "Chính Kịch", "slug": "chinh-kich"}, {"id": "bb2b4b030608ca5984c8dd0770f5b40b", "name": "Tình Cảm", "slug": "tinh-cam"}, {"id": "f8ec3e9b77c50