INSERT INTO public.movies (id, movie_id, slug, name, origin_name, poster_url, thumb_url, year, type, quality, lang, "time", view, description, status, trailer_url, sections, section, is_recommended, categories, countries, actors, directors, modified_at, episode_current, episode_total) VALUES (332, 'f45fe3f64d37932f037761c6bf9a46db', 'tay-du-ky-phan-1', 'Tây Du Ký (Phần 1)', 'Journey To The West (Season 1)', 'https://phimimg.com/upload/vod/20250814-1/f39af4b6efc136cfe442f839161a5f63.jpg', 'https://phimimg.com/upload/vod/20250814-1/331ba7ce5ee7495c00b9bd9ddcd1bd37.jpg', 1986, 'series', 'FHD', 'Thuyết Minh + Lồng Tiếng', '45 phút/tập', 0, 'Bộ phim ra đời trong hoàn cảnh khó khăn do được sản xuất vào thập kỉ 1980 thời mà kĩ xảo điện ảnh còn thô sơ lạc hậu. Vốn đầu tư 6 triệu nhân dân tệ tuy là một khoản tiền rất lớn nhưng cũng không đủ trang trải cho toàn bộ phim, các diễn viên cũng chỉ nhận được thù lao tượng trưng, ít ỏi. Phim được làm trong 6 năm trời ròng rã từ năm 1982 đến năm 1988 hoàn thành. Nhiều thông tin cung cấp năm 1986 bộ phim chính thức trình chiếu cho khán thính giả và nhận thấy có nhiều phần thiếu sót nên tiếp tục đầu tư xây dựng và mãi đến năm 1988 thì chính thức hoàn thiện. &nbsp;Đặc biệt, trong 20 năm qua, phim đã nhiều lần phát sóng lại, mỗi lần cả người lớn và trẻ em đều háo hức đón xem. Có thể nói, bản phim Tây Du Kí 1986 đã sống mãi với thời gian và ăn sâu trong kí ức của nhiều thế hệ khán giả. Hầu hết các đài truyền hình của Việt Nam đều ít nhất trình chiếu một lần. Có đài phát sóng đến gần 10 lần và trình chiếu đi và chiếu lại suốt nhiều năm liền.', 'completed', 'https://www.youtube.com/watch?v=K3KNpyKILHM', NULL, NULL, false, '[{"id": "9822be111d2ccc29c7172c78b8af8ff5", "name": "Hành Động", "slug": "hanh-dong"}, {"id": "66c78b23908113d478d8d85390a244b4", "name": "Phiêu Lưu", "slug": "phieu-luu"}, {"id": "37a7b38b6184a5ebd3c43015aa20709d", "name": "Chính Kịch", "slug": "chinh-kich"}, {"id": "2276b29204c46f75064735477890afd6", "name": "Thần Thoại